import axios from "axios";

function LoginPage() {
    function logar() {
        let email = document.querySelector("#email").value;
        let senha = document.querySelector("#senha").value;

        if (!email || !senha) return alert("Dados incompletos.");

        axios.post('http://localhost:5300/api/users/logar', {
            email: email, 
            senha: senha
        }).then(response => {
            if (response.data.erro) return alert(response.data.erro);
            else window.location.href = '/privado';
        }).catch(erro => {return alert(erro.data.erro);});
    }

    function cadastrar() {
        let email = document.querySelector("#email").value;
        let senha = document.querySelector("#senha").value;

        if (!email || !senha) return alert("Dados incompletos.");

        axios.post('http://localhost:5300/api/users/cadastrar', {
            email: email, 
            senha: senha
        }).then(response => {
            if (response.data.erro) return alert(response.data.erro);
        }).catch(erro => {return alert(erro.data.erro);});
    }

    return (
    <div className="LoginPageBody">
        <form>
        <label>Email:</label>
        <input id="email" type="email"/>
        <input id="senha" type="password"/>
        <button onClick={() => {logar()}}></button>
        <button onClick={() => {cadastrar()}}></button>
        </form>
    </div>
    );
}

export default LoginPage;
