import React from "react"
import axios from 'axios'
import strip from './../strip.jsx'

const Event = props => {
    async function removeEvent() {
        console.log("Deleting card " + props.event._id);
        await axios.delete("http://localhost:5300/card/"+props.event._id);
        props.refresh();
    }

    async function saveEvent() {
        console.log("Saving")
        let tituloVal = document.getElementById("description:"+props.event._id).innerHTML;
        let startVal = document.getElementById("start:"+props.event._id).innerHTML;
        let endVal = document.getElementById("end:"+props.event._id).innerHTML;

        tituloVal = strip(tituloVal);
        startVal = strip(startVal);
        endVal = strip(endVal);

        console.log("Updating event " + props.event._id);

        await axios.put("http://localhost:5300/card/"+props.event._id, {
            "description":tituloVal,
            "start":startVal,
            "end":endVal
        });

        props.refresh();
    }

    console.log("Estou dentro do evento e vou printar ele:");
    console.log(props.event);
    console.log("\n\n\n");

    return (
        <div className="card">
            <div className="header-card">
                <button className="card-button save-card-button" onClick={() => saveEvent()}>save</button>
                <button className="card-button remove-card-button" onClick={() => removeEvent()}>x</button>
            </div>
            <h3 contentEditable="true" id={"description:"+props.card._id}>{props.event.description}</h3>
            <p  contentEditable="true" className="description" id={"start:"+props.event._id}>{props.event.start}</p>
            <p  contentEditable="true" className="description" id={"end:"+props.event._id}>{props.event.end}</p>
        </div>
    )
}


export default Event
