import React, { useState } from "react"
import axios from 'axios'
import strip from './../strip.jsx';

const Popup = props => {
    if (!props.event) return (<p>Erro no popup</p>)

    async function removeEvent() {
        console.log("Deleting event " + props.event._id);
        await axios.delete("http://localhost:5300/myNotes/"+props.event._id);
        closePopup();
        props.refresh();
    }

    async function saveEvent() {
        let descriptionVal = document.getElementById("description:"+props.event._id).innerHTML;
        let startValDate = document.getElementById("startdate:"+props.event._id).value;
        let startValHour = document.getElementById("starthour:"+props.event._id).value;
        let endValDate = document.getElementById("enddate:"+props.event._id).value;
        let endValHour = document.getElementById("endhour:"+props.event._id).value;

        descriptionVal = await strip(descriptionVal);

        await axios.put("http://localhost:5300/myNotes/"+props.event._id, {
            description:descriptionVal,
            startDate:startValDate,
            startHour:startValHour,
            endDate:endValDate,
            endHour:endValHour
        });

        closePopup();
        props.event.refresh();
    }

    function closePopup() {
        props.closePopup();
        props.event.refresh();
    }

    return (
        <div className="popup-background">
            <div className="popup-body">
            <div className="header-event">
                <button className="event-button save-event-button" onClick={() => saveEvent()}>save</button>
                <button className="event-button remove-event-button" onClick={() => removeEvent()}>Remove</button>
                <button className="event-button" onClick={closePopup}>x</button>
            </div>
            <p className="description" contentEditable="true" id={"description:"+props.event._id}>{props.event.description}</p>
            <input id={"starthour:"+props.event._id} type="time" defaultValue={props.event.startHour}/>
            <input id={"startdate:"+props.event._id} type="date" defaultValue={props.event.startDate}/>
            <input id={"endhour:"+props.event._id}type="time"    defaultValue={props.event.endHour}/>
            <input id={"enddate:"+props.event._id} type="date"   defaultValue={props.event.endDate}/>
            </div>
        </div>
    )
}

export default Popup
