import axios from "axios";
import { Link, useNavigate  } from "react-router-dom"
import { useState, useEffect } from 'react'
import { register } from "../Actions/userActions";
import { useCookies } from "react-cookie";
import Lista from "../Components/listEvents"

function MyNotes() {
    const [email, setEmail] = useState("");
    const [id, setId] = useState("");
    const [cookies, setCookie, removeCookie] = useCookies(['user']);

    const [currState, setState] = useState({ 'events' : [], 'pulledEvents' : false });

    const navigate = useNavigate ();
    useEffect(() => {
        if (!cookies.user) {
            navigate("/login");
        } else {
            axios.post("http://localhost:5300/api/users/token", {token: cookies.user})
            .then(res => {
                setEmail(res.data.email);
                setId(res.data.id);
            });
        }
    })

    if (!currState.pulledEvents) pullEvents();

    function pullEvents() {
        console.log("Pulling events");
        axios.get("http://localhost:5300/mynotes/" + id.toString())
        .then(res => {
            let events = res['data'];
            let stateCopy = JSON.parse(JSON.stringify(currState));
            stateCopy.events = events;
            stateCopy.pulledEvents = true;

            setState(stateCopy);
        })
    }

    function changePulledEvents() {
        let stateCopy = JSON.parse(JSON.stringify(currState));
        stateCopy.pulledEvents = false;
        setState(stateCopy);
    }

    function listas() {
        return (<Lista key={id} id={id} refresh={changePulledEvents}/>)
    }

    return (
    <div className="MyNotesNody">
        OLA {email} tudo bem? {id}
    </div>
    );
}

export default MyNotes;
