import axios from "axios";
import { Link } from "react-router-dom"
import { useState, useEffect } from 'react'
import { register } from "../Actions/userActions";
import { useCookies } from "react-cookie";

function RegisterPage({ history }) {
    const [email, setEmail] = useState("");
    const [password, setPassword] = useState("");
    const [confirmpassword, setConfirmPassword] = useState("");
    const [message, setMessage] = useState(null);

    const [cookies, setCookie] = useCookies(["user"]);

    const cadastrar = (e) => {
        e.preventDefault();
        if (!email || !password) return alert("Dados incompletos.");

        if (password !== confirmpassword) {
            setMessage("Passwords do not match");
            return;
        } 

        const data = register(email, password);

        setCookie('user', data.token.toString());
    }

    return (
    <div className="RegisterPageBody">
        {message && <p>{message}</p>}
        <form onSubmit={cadastrar}>
        <label>Email:</label>
        <input id="email" type="email" placeholder="email" onChange={(e) => setEmail(e.target.value)}/>
        <input id="senha" type="password" placeholder="password" onChange={(e) => setPassword(e.target.value)}/>
        <input id="senha" type="password" placeholder="confirm password" onChange={(e) => setConfirmPassword(e.target.value)}/>
        <button type="submit">Cadastrar</button>
        <Link to="/login">JÃ¡ possui cadastro?</Link>
        </form>
    </div>
    );
}

export default RegisterPage;
