import React from "react"
import axios from 'axios'
import strip from './../strip.jsx'

const Event = props => {
    async function removeEvent() {
        console.log("Deleting card " + props.event._id);
        await axios.delete("http://localhost:5300/myNotes/"+props.event._id);
        props.refresh();
    }

    function getDate() {
        var date = new Date();
        var year= date.getFullYear();

        var month = date.getMonth()+1;
        if(month<10)
            month="0"+month;

        var day = date.getDate();
        if(day<10)
            day = "0"+day;

        return year+"-"+month+"-"+day;
    }

    async function saveEvent() {
        let tituloVal = document.getElementById("description:"+props.event._id).innerHTML;
        let startVal = document.getElementById("start:"+props.event._id).value;
        let endVal = document.getElementById("end:"+props.event._id).value;

        tituloVal = await strip(tituloVal);

        console.log("Updating event " + props.event._id);
        console.log(tituloVal);
        console.log(startVal);
        console.log(endVal);

        // await axios.put("http://localhost:5300/myNotes/"+props.event._id, {
        //     description:tituloVal,
        //     start:startVal,
        //     end:endVal
        // });

        props.refresh();
    }

    return (
        <div className="card">
            <div className="header-card">
                <button className="card-button save-card-button" onClick={() => saveEvent()}>save</button>
                <button className="card-button remove-card-button" onClick={() => removeEvent()}>x</button>
            </div>
            <h3 contentEditable="true" id={"description:"+props.event._id}>{props.event.description}</h3>
            <input id={"start:"+props.event._id} type="date"/><input type="time"/>
            <input id={"end:"+props.event._id} type="date"/><input type="time"/>
        </div>
    )
}


export default Event
