import axios from "axios";
import { Link, Navigate } from "react-router-dom";
import { useCookies } from "react-cookie";
import { useState } from "react";
import Navbar from "../Components/navbar";
import "./css/loginPage.css"

function LoginPage() {
    const [cookies, setCookie] = useCookies(["user"]);
    const [email, setEmail] = useState(null);

    async function logar(e) {
        e.preventDefault();
        let email = document.querySelector("#email").value;
        let senha = document.querySelector("#senha").value;

        if (!email || !senha) return alert("Dados incompletos.");

        axios.post('http://localhost:5300/api/users/logar', {
            email: email, 
            senha: senha
        }).then(response => {
            console.log(response);
            setEmail(response.data.email);
            setCookie('user', response.data.token);
        }).catch(erro => {return alert(erro.data.erro);});
    }

    return (
    <>
    <Navbar/>
    <div className="LoginPageBody">
        {email && <Navigate to="/myEvents"/>}
        <form>
        <label>Email:</label>
        <input id="email" type="email" placeholder="email"/>
        <input id="senha" type="password" placeholder="password"/>
        <button onClick={logar}>Login</button>
        <Link to="/register">NÃ£o possui cadastro?</Link>
        </form>
    </div>
    </>
    );
}

export default LoginPage;
