import React, { useState } from "react"
import Event from "./event";
import axios from 'axios'

const Lista = props => {

    const [currState, setState] = useState({"events":[], "pulledEvents":false});

    if (!currState["pulledEvents"]) {
        console.log(props.id);
        axios.get('http://localhost:5300/myNotes/' + props.id)
            .catch(e => console.log("No cards to pull"))
            .then(function (response) {
                setState({
                    ...currState,
                    cards: response ? response.data : [],
                    pulledEvents: true
                })
            })
    }

    async function addEmptyEvent() {
        const date = new Date().toLocaleString();
        await axios.post('http://localhost:5300/myNotes/' + props.id, {
            description:"Novo evento",
            start: date,
            start: date
        })
        let stateCopy = JSON.parse(JSON.stringify(currState));
        stateCopy.pulledEvents = false;
        setState(stateCopy);
    }

    function events() {
        return currState['events'].map(event => <Event key={event._id} card={event} refresh={changePulledEvents}/>);
    }     

    function changePulledEvents() {
        let stateCopy = JSON.parse(JSON.stringify(currState));
        stateCopy.pulledEvents = false;
        setState(stateCopy);
    }

    return (
        <div className="cardList">
            <input id={"text:"+props.lista._id} type="text" className="titleText" defaultValue={props.lista.titulo}/>
            <div className="cardContainer">
                {events()}
                <button id="newCardButton" onClick={addEmptyEvent}>+</button>
            </div>
        </div>
    )
}

export default Lista
