import axios from "axios";
import { Link } from "react-router-dom"
import { useState } from 'react'

function RegisterPage({ history }) {
    const [email, setEmail] = useState("");
    const [password, setPassword] = useState("");
    const [confirmpassword, setConfirmPassword] = useState("");
    const [message, setMessage] = useState(null);
    const [picMessage, setPicMessage] = useState(null);

    const dispatch = useDispatch();

    const userRegister = useSelector((state) => state.userRegister);
    const { loading, error, userInfo } = userRegister;

    useEffect(() => {
        if (userInfo) {
            history.push("/");
        }
    }, [history, userInfo]);

    const cadastrar = (e) => {
        e.preventDefault();
        if (!email || !password) return alert("Dados incompletos.");

        axios.post('http://localhost:5300/api/users/cadastrar', {
            email: email, 
            senha: password
        })
        .then(res => {
            alert(res);
        })
    }

    return (
    <div className="RegisterPageBody">
        <form onSubmit={cadastrar}>
        <label>Email:</label>
        <input id="email" type="email" placeholder="email" onChange={(e) => setEmail(e.target.value)}/>
        <input id="senha" type="password" placeholder="password" onChange={(e) => setPassword(e.target.value)}/>
        <input id="senha" type="password" placeholder="confirm password" onChange={(e) => setConfirmPassword(e.target.value)}/>
        <button type="submit">Cadastrar</button>
        <Link to="/login">JÃ¡ possui cadastro?</Link>
        </form>
    </div>
    );
}

export default RegisterPage;
