import { Link } from "react-router-dom"
import { useDispatch, useSelector } from "react-redux";
import { useState, useEffect } from 'react'
import { register } from "../Actions/userActions";

function RegisterPage({ history }) {
    const [email, setEmail] = useState("");
    const [password, setPassword] = useState("");
    const [confirmpassword, setConfirmPassword] = useState("");
    const [message, setMessage] = useState(null);

    useEffect(() => {
        if (userInfo) {
            history.push("/");
        }
    }, [history, userInfo]);

    const cadastrar = (e) => {
        e.preventDefault();
        if (!email || !password) return alert("Dados incompletos.");

        if (password !== confirmpassword) {
            setMessage("Passwords do not match");
        } else dispatch(register(email, password));
    }

    return (
    <div className="RegisterPageBody">
        <form onSubmit={cadastrar}>
        <label>Email:</label>
        <input id="email" type="email" placeholder="email" onChange={(e) => setEmail(e.target.value)}/>
        <input id="senha" type="password" placeholder="password" onChange={(e) => setPassword(e.target.value)}/>
        <input id="senha" type="password" placeholder="confirm password" onChange={(e) => setConfirmPassword(e.target.value)}/>
        <button type="submit">Cadastrar</button>
